import React, { useState, useEffect } from 'react';
import './Mbook.css';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Mbook = () => {
  const location = useLocation();
  const { serviceName, artistName, demoImages, email } = location.state || {}; // Get email from location.state
  const [message, setMessage] = useState('');
  const [isNavActive, setIsNavActive] = useState(false);
  const [handType, setHandType] = useState('half');
  const [numberOfHands, setNumberOfHands] = useState(0);
  const [totalPrice, setTotalPrice] = useState(0);
  const [address, setAddress] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const [profData, setProfData] = useState(null); // State to store profReg data
  const [profEmail, setProfEmail] = useState(email || ''); // Correctly set profEmail

  console.log(profEmail);  // This will now correctly log profEmail in the console

  useEffect(() => {
    console.log('Email passed to Mbook:', email);
    // Fetch professional details based on the email
    const fetchProfData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/profReg/${email}`);
        setProfData(response.data);
      } catch (error) {
        console.error('Error fetching professional data:', error);
      }
    };

    if (email) {
      fetchProfData();
    }
  }, [email]);

  const calculateTotal = () => {
    const price = handType === 'half' ? 500 : 1000;
    setTotalPrice(price * numberOfHands);
  };

  const handleBooking = async () => {
    if (!address || !startTime || !endTime || !userEmail) {
      alert('Please fill out all required fields.');
      return;
    }

    const formData = {
      handType,
      numberOfHands,
      address,
      startTime,
      endTime,
      userEmail,
      totalPrice,
      profEmail
    };
    console.log(formData);

    try {
      const response = await axios.post('http://localhost:5000/book', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      setMessage('Registration successful');
      setTimeout(() => {
        setMessage('');
        // setHandType('')
        // setNumberOfHands('')
        // setAddress('')
        // setHandType('')
        // setStartTime('')
        // setEndTime('')
        // setUserEmail('')
      }, 2000);
    } catch (error) {
      alert('Error sending booking request');
    }
  };

  return (
    <div className="mbook-container">
      <h1>Book {serviceName}
        <br />
      </h1>
      <div><p>Service By  {artistName}<br />
        Email: {email}</p>
      </div>

      <div className="mehndi-image">
        {demoImages && demoImages.length > 0 ? (
          demoImages.map((image, index) => (
            <img
              key={index}
              src={`http://localhost:5000/uploads/${image}`}
              alt={`Artist work ${index}`}
              style={{ width: '200px', margin: '10px' }}
            />
          ))
        ) : (
          <p>No images available</p>
        )}
      </div>

      <div className="form-container">
        {message && <h2 className='msg'>{message}</h2>}

        <form>
          <div className="form-group">
            <label htmlFor="handType">Select Hand Type:</label>
            <select id="handType" value={handType} onChange={(e) => setHandType(e.target.value)}>
              <option value="half" data-price="500">Half Hand</option>
              <option value="full" data-price="1000">Full Hand</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="numberOfHands">Number of Hands:</label>
            <input
              type="number"
              id="numberOfHands"
              min="1"
              value={numberOfHands}
              onChange={(e) => setNumberOfHands(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="address">Enter Address:</label>
            <input
              type="text"
              id="address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Enter your address"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="startTime">Start Date & Time:</label>
            <input
              type="datetime-local"
              id="startTime"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="endTime">End Date & Time:</label>
            <input
              type="datetime-local"
              id="endTime"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="userEmail">Your Email:</label>
            <input
              type="email"
              id="userEmail"
              value={userEmail}
              onChange={(e) => setUserEmail(e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>
          <button className="btncal" type="button" onClick={calculateTotal}>Calculate Total Price</button>
          <button className="btncal" type="button" onClick={handleBooking}>Book Now</button>
        </form>
        <div className="total-price" onChange={(e) => setTotalPrice(e.target.value)}>Total Price: ₹{totalPrice}</div>
      </div>
    </div>
  );
};

export default Mbook;
