import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown, faComment, faUserCircle } from '@fortawesome/free-solid-svg-icons';
import './shorts.css';

const videoData = [
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 2", description: "Description for Project 2" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 2", description: "Description for Project 2" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 2", description: "Description for Project 2" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 4", description: "Description for Project 4" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 3", description: "Description for Project 3" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 1", description: "Description for Project 1" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 2 (Duplicate)", description: "Another Description for Project 2" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 2", description: "Description for Project 2" },
    { src: require("D:\TechTalentConnect\frontend\src\components\videos\WhatsApp Video 2024-09-12 at 19.10.09_5c29ceab.mp"), title: "Project 4 (Duplicate)", description: "Another Description for Project 4" },
];

const Shorts = () => {
    const [reactions, setReactions] = useState(videoData.map(() => ({ liked: false, disliked: false })));
    const [descriptionVisible, setDescriptionVisible] = useState(videoData.map(() => false));
    const [commentVisible, setCommentVisible] = useState(videoData.map(() => false));

    const handleReaction = (index, reactionType) => {
        setReactions((prevReactions) => {
            const newReactions = [...prevReactions];

            // If not already reacted, update the reaction type
            if (!newReactions[index][reactionType]) {
                newReactions[index][reactionType] = true;

                // Reset the opposite reaction to false
                newReactions[index][reactionType === 'liked' ? 'disliked' : 'liked'] = false;
            }

            return newReactions;
        });
    };

    const toggleDescription = (index) => {
        setDescriptionVisible((prevVisibility) => {
            const newVisibility = [...prevVisibility];
            newVisibility[index] = !newVisibility[index];
            return newVisibility;
        });
    };

    const toggleComment = (index) => {
        setCommentVisible((prevVisibility) => {
            const newVisibility = [...prevVisibility];
            newVisibility[index] = !newVisibility[index];
            return newVisibility;
        });
    };

    return (
        <div className="videos-container">
            {videoData.map((video, index) => (
                <div key={index} className="video-card">
                    <video controls>
                        <source src={video.src} type="video/mp4" />
                        Your browser does not support the video tag.
                    </video>
                    <div className="interaction">
                        <button>
                            <FontAwesomeIcon icon={faUserCircle} />
                        </button>
                        <button
                            onClick={() => handleReaction(index, 'liked')}
                            disabled={reactions[index].liked} // Disable after like is clicked
                        >
                            <FontAwesomeIcon icon={faThumbsUp} />
                            <p className="ptag">{reactions[index].liked ? 1 : 0}</p>
                        </button>
                        <button
                            onClick={() => handleReaction(index, 'disliked')}
                            disabled={reactions[index].disliked} // Disable after dislike is clicked
                        >
                            <FontAwesomeIcon icon={faThumbsDown} />
                            <p className="ptag">{reactions[index].disliked ? 1 : 0}</p>
                        </button>
                        <button onClick={() => toggleComment(index)}>
                            <FontAwesomeIcon icon={faComment} />
                        </button>
                    </div>

                    {descriptionVisible[index] && <p className="video-description">{video.description}</p>}
                    {commentVisible[index] && (
                        <div className="comment-section">
                            <textarea placeholder="Write a comment..." className="comment-input"></textarea>
                            <button className="submit-comment">Submit</button>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default Shorts;
