import React, { useState, useEffect } from 'react';
import './notifications.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Notifications = (props) => {
    const [notifications, setNotifications] = useState([]);
    const [isNavActive, setIsNavActive] = useState(false);
    
    const toggleNav = () => {
        setIsNavActive(!isNavActive);
    };

    useEffect(() => {
        const fetchNotifications = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/getNotifications/${props.email}`);
                setNotifications(response.data);  // Assuming backend returns notifications for the user
            } catch (error) {
                console.error('Error fetching notifications:', error);
            }
        };

        if (props.email) {
            fetchNotifications();
        }
    }, [props.email]);

    return (
        <>
            <nav className="navbar">
                <div className="logo">
                    <h1 style={{ fontFamily: "Georgia, 'Times New Roman', Times, serif", fontSize: '27px' }}>
                        SkillBridge
                    </h1>
                </div>
                <div className="nav-right">
                    <ul className={`nav-links ${isNavActive ? 'active' : ''}`} id="navLinks">
                        <li><Link to="/AHome">Home</Link></li>
                        <li><Link to="/myworks">My Works</Link></li>
                        <li><Link to="/notifications">Notifications</Link></li>
                    </ul>
                    <div className="profile">
                        <img
                            src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
                            alt="Profile"
                            className="profile-img"
                        />
                    </div>
                    <div className="hamburger" id="hamburger" onClick={toggleNav}>
                        <span className="bar"></span>
                        <span className="bar"></span>
                        <span className="bar"></span>
                    </div>
                </div>
            </nav>
            <div className="Notifications-container">
                <h1>Notifications</h1>
                <ul>
                    {notifications.length > 0 ? (
                        notifications.map((notification, index) => (
                            <li key={index}>
                                <p>{notification.message}</p>
                                <p><strong>Time:</strong> {new Date(notification.time).toLocaleString()}</p>
                            </li>
                        ))
                    ) : (
                        <p>No notifications available</p>
                    )}
                </ul>
            </div>
        </>
    );
};

export default Notifications;
