import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp, faThumbsDown, faComment, faUserCircle } from '@fortawesome/free-solid-svg-icons';
import './shorts.css';

const Shorts = () => {
    const [data, setData] = useState([]);
    const [reactions, setReactions] = useState([]);
    const [descriptionVisible, setDescriptionVisible] = useState([]);
    const [commentVisible, setCommentVisible] = useState([]);
    const [formData, setFormData] = useState({ fullName: '', demoVideos: [] }); // Form data state
    const [file, setFile] = useState(null); // State to handle file upload

    useEffect(() => {
        axios.get('http://localhost:5000/api/professionals')
            .then((res) => {
                setData(res.data);
                setReactions(res.data.map(() => ({ liked: false, disliked: false })));
                setDescriptionVisible(res.data.map(() => false));
                setCommentVisible(res.data.map(() => false));
            })
            .catch((err) => {
                console.log(err);
            });
    }, []);

    const handleReaction = (index, reactionType) => {
        setReactions((prevReactions) => {
            const newReactions = [...prevReactions];
            if (!newReactions[index][reactionType]) {
                newReactions[index][reactionType] = true;
                newReactions[index][reactionType === 'liked' ? 'disliked' : 'liked'] = false;
            }
            return newReactions;
        });
    };

    const toggleDescription = (index) => {
        setDescriptionVisible((prevVisibility) => {
            const newVisibility = [...prevVisibility];
            newVisibility[index] = !newVisibility[index];
            return newVisibility;
        });
    };

    const toggleComment = (index) => {
        setCommentVisible((prevVisibility) => {
            const newVisibility = [...prevVisibility];
            newVisibility[index] = !newVisibility[index];
            return newVisibility;
        });
    };

    // Handle form input change
    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Handle file input change
    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    // Handle form submit
    const handleSubmit = async (e) => {
        e.preventDefault();

        const form = new FormData();
        form.append('fullName', formData.fullName);
        form.append('demoVideos', file);

        try {
            const response = await axios.post('http://localhost:5000/ProfReg', form, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log(response.data);
            // Optionally refresh the list of professionals after submission
            const res = await axios.get('http://localhost:5000/api/professionals');
            setData(res.data);
        } catch (error) {
            console.log('Error uploading:', error);
        }
    };

    return (
        <div className="gallery-container">
            {/* Form to add new professional */}
            <form onSubmit={handleSubmit}>
                <label>
                    Full Name:
                    <input type="text" name="fullName" value={formData.fullName} onChange={handleInputChange} />
                </label>
                <label>
                    Upload Demo Video:
                    <input type="file" onChange={handleFileChange} />
                </label>
                <button type="submit">Submit</button>
            </form>

            {/* Display the fetched professionals */}
            {data.length > 0 ? (
                data.map((item, index) => (
                    <div key={index} className="content">
                        <h2>{item.fullName}</h2>
                        {item.demoVideos && item.demoVideos.map((video, vidIndex) => (
                            <div key={vidIndex} className="video-container">
                                <video controls width="300">
                                    <source src={`http://localhost:5000/uploads/${video}`} type="video/mp4" />
                                    Your browser does not support the video tag.
                                </video>
                                <div className="interaction">
                                    <button>
                                        <FontAwesomeIcon icon={faUserCircle} />
                                    </button>
                                    <button
                                        onClick={() => handleReaction(index, 'liked')}
                                        disabled={reactions[index].liked}
                                    >
                                        <FontAwesomeIcon icon={faThumbsUp} />
                                        <p className="ptag">{reactions[index].liked ? 1 : 0}</p>
                                    </button>
                                    <button
                                        onClick={() => handleReaction(index, 'disliked')}
                                        disabled={reactions[index].disliked}
                                    >
                                        <FontAwesomeIcon icon={faThumbsDown} />
                                        <p className="ptag">{reactions[index].disliked ? 1 : 0}</p>
                                    </button>
                                    <button onClick={() => toggleComment(index)}>
                                        <FontAwesomeIcon icon={faComment} />
                                    </button>
                                </div>

                                {descriptionVisible[index] && <p className="video-description">{item.description}</p>}
                                {commentVisible[index] && (
                                    <div className="comment-section">
                                        <textarea placeholder="Write a comment..." className="comment-input"></textarea>
                                        <button className="submit-comment">Submit</button>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                ))
            ) : (
                'No data found'
            )}
        </div>
    );
};

export default Shorts;
