import React, { useState, useEffect } from 'react';
import './Mbook.css';
import { Link } from 'react-router-dom';

const Mbook = () => {
  const [isNavActive, setIsNavActive] = useState(false);
  const [handType, setHandType] = useState('half');
  const [numberOfHands, setNumberOfHands] = useState(1);
  const [totalPrice, setTotalPrice] = useState(0);
  const [address, setAddress] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const messages = [
    "Book a Mehndi Artist Now!",
    "Get the best designs at your doorstep.",
    "Book early to secure your slot!"
  ];
  const [displayedText, setDisplayedText] = useState('');
  const [currentMessageIndex, setCurrentMessageIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  const toggleNav = () => {
    setIsNavActive(!isNavActive);
  };

  const calculateTotal = () => {
    const price = handType === 'half' ? 500 : 1000;
    setTotalPrice(price * numberOfHands);
  };

  const handleBooking = async () => {
    const formData = {
      handType,
      numberOfHands,
      address,
      startTime,
      endTime,
      userEmail,
      price
    };
  
    try {
      const response = await fetch('http://localhost:5000/book', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
  
      const result = await response.text();
      alert(result);
    } catch (error) {
      alert('Error sending booking request');
    }
  };

  useEffect(() => {
    const currentMessage = messages[currentMessageIndex];
    if (charIndex < currentMessage.length) {
      const timeout = setTimeout(() => {
        setDisplayedText(prev => prev + currentMessage[charIndex]);
        setCharIndex(charIndex + 1);
      }, 100); // Adjust speed as needed
      return () => clearTimeout(timeout);
    } else {
      // Move to the next message after finishing the current one
      if (currentMessageIndex < messages.length - 1) {
        setTimeout(() => {
          setCurrentMessageIndex(currentMessageIndex + 1);
          setCharIndex(0);
        }, 1000); // Delay before starting the next message
      }
    }
  }, [charIndex, currentMessageIndex, messages]);

  return (
    <>
      <nav className="navbar">
        <div className="logo">
          <h1 style={{ fontFamily: "Georgia, 'Times New Roman', Times, serif", fontSize: '27px' }}>
            SkillBridge
          </h1>
        </div>
        <div className="nav-right">
          <ul className={`nav-links ${isNavActive ? 'active' : ''}`} id="navLinks">
            <li><Link to="/">Home</Link></li>
            <li><Link to="/videos">Videos</Link></li>
            <li><Link to="/services">Services</Link></li>
            <li><Link to="/booked-services">Booked Services</Link></li>
          </ul>
          <div className="profile">
            <img
              src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"
              alt="Profile"
              className="profile-img"
            />
          </div>
          <div className="hamburger" id="hamburger" onClick={toggleNav}>
            <span className="bar"></span>
            <span className="bar"></span>
            <span className="bar"></span>
          </div>
        </div>
      </nav>
      <div className="mbook-container">
        <div className="message-container">
          <div className="typing-message">
            {displayedText}
          </div>
          <div className="mehndi-image">
            <img
              src="https://www.shutterstock.com/image-photo/mehndi-artist-making-bridal-hand-260nw-2345429273.jpg"
              alt="Mehndi Design"
            />
          </div>
        </div>

        <div className="form-container">
          <h2>Book Mehndi Artist</h2>
          <form>
            <div className="form-group">
              <label htmlFor="handType">Select Hand Type:</label>
              <select id="handType" value={handType} onChange={(e) => setHandType(e.target.value)}>
                <option value="half" data-price="500">Half Hand</option>
                <option value="full" data-price="1000">Full Hand</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="numberOfHands">Number of Hands:</label>
              <input
                type="number"
                id="numberOfHands"
                min="1"
                value={numberOfHands}
                onChange={(e) => setNumberOfHands(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="address">Enter Address:</label>
              <input
                type="text"
                id="address"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="Enter your address"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="startTime">Start Date & Time:</label>
              <input
                type="datetime-local"
                id="startTime"
                value={startTime}
                onChange={(e) => setStartTime(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="endTime">End Date & Time:</label>
              <input
                type="datetime-local"
                id="endTime"
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="userEmail">Your Email:</label>
              <input
                type="email"
                id="userEmail"
                value={userEmail}
                onChange={(e) => setUserEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            <button className="btncal" type="button" onClick={calculateTotal}>Calculate Total Price</button>
            <button className="btncal" type="button" onClick={handleBooking}>Book Now</button>
          </form>
          <div className="total-price" >Total Price: ₹{totalPrice}</div>
        </div>
      </div>
    </>
  );
};

export default Mbook;
